import { createStore } from "redux";

import mainReducer from "../reducers/index";

const usestore = createStore(mainReducer, window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__());

export default usestore;
